import './Form.css'
import React, { useState } from 'react'

const Form = props =>{
    const [currSavings,setCurrSavings] = useState(0);
    const [yearlySavings,setYearlySavings] = useState(0);
    const [expReturn,setExpReturn] = useState(0);
    const [duration,setDuration] = useState(0);
    const currSavingsHandler = event => {
        setCurrSavings(event.target.value);
    }
    const yearlySavingsHandler = event => {
        setYearlySavings(event.target.value)
    }
    const expReturnHandler = event => {
        setExpReturn(event.target.value)
    }
    const durationHandler = event => {
        setDuration(event.target.value);
    }
    const formSubmitHandler = event => {
        event.preventDefault();
        const formData = {
            currSavings,
            yearlySavings,
            expReturn,
            duration
        }
        props.onSubmit();
    }

    return (
        <div>
        <form className="form" onSubmit = {formSubmitHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={currSavingsHandler}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange={yearlySavingsHandler}/>
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" onChange={expReturnHandler}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange={durationHandler}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form></div>
    )
}


export default Form;
